import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

const scheduleData = [
  {
    time: '08:40 ~ 09:00',
    title: '등록 및 1일차 출석 확인',
    place: '1층, 스터디 카페',
    highlight: false,
  },
  {
    time: '09:10 ~ 09:50',
    title: '개회식, 안전교육, 캠프 안내, 기념사진',
    place: '4층, 시청각실',
    highlight: false,
  },
  {
    time: '10:00 ~ 11:30',
    title: '기조강연 1 : 창업과 혁신의 이해',
    place: '4층, 시청각실',
    highlight: true,
  },
  {
    time: '11:30 ~ 13:00',
    title: '점심식사',
    place: '자유 공간',
    highlight: false,
  },
  {
    time: '13:00 ~ 14:30',
    title: '기조강연 2 : 벤처기업 창업사례 공유',
    place: '4층, 시청각실',
    highlight: false,
  },
  {
    time: '14:30 ~ 14:50',
    title: '분야별 교실 배정, 이동',
    place: '이동',
    highlight: false,
  },
  {
    time: '15:00 ~ 18:00',
    title: '아이디어 도출 및 사업성 검토',
    place: '각 교실',
    highlight: false,
  },
];

type TimelineRowProps = {
  time: string;
  title: string;
  place: string;
  highlight: boolean;
  isFirst: boolean;
  isLast: boolean;
};

function TimelineRow({ time, title, place, highlight, isFirst, isLast }: TimelineRowProps) {
  return (
    <div className="timeline-row-single">
      <div className="timeline-dotcol-single">
        <div className={`timeline-dot-single${highlight ? ' highlight' : ''}`}></div>
        {!isLast && <div className="timeline-line-single"></div>}
      </div>
      <div className="timeline-time-single">{time}</div>
      <div className={`timeline-bar-single${highlight ? ' highlight' : ''}`}></div>
      <div className="timeline-desc-single">
        <div>{title}</div>
        <div>{place}</div>
      </div>
    </div>
  );
}

function Schedule() {
  return (
    <div className="main-bg">
      <nav className="main-nav">
        <div className="nav-left"></div>
        <ul className="nav-menu">
          <li><Link to="/">홈</Link></li>
          <li><Link to="/about">소개</Link></li>
          <li><Link to="/schedule">일정</Link></li>
          <li><Link to="/award">수상</Link></li>
        </ul>
        <button className="apply-btn">지원하기</button>
      </nav>
      <div style={{ marginTop: '40px' }}>
        {scheduleData.map((item, idx) => (
          <TimelineRow
            key={idx}
            time={item.time}
            title={item.title}
            place={item.place}
            highlight={item.highlight}
            isFirst={idx === 0}
            isLast={idx === scheduleData.length - 1}
          />
        ))}
      </div>
    </div>
  );
}

export default Schedule;
