import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

const scheduleData = [
  {
    date: 'Thu 31',
    items: [
      {
        time: '08:40 - 09:00',
        title: '등록 및 1일차 출석 확인',
        place: '1층, 스터디카페',
        highlight: false,
      },
      {
        time: '09:10 - 09:50',
        title: '개회식, 안전교육, 캠프 안내, 기념사진',
        place: '4층, 시청각실',
        highlight: false,
      },
      {
        time: '10:00 - 11:30',
        title: '기조강연 1 : 창업과 혁신의 이해',
        place: '4층, 시청각실',
        highlight: true,
      },
      {
        time: '11:30 - 13:00',
        title: '점심식사',
        place: '자유 공간',
        highlight: false,
      },
      {
        time: '13:00 - 14:30',
        title: '기조강연 2 : 벤처기업 창업사례 공유',
        place: '4층, 시청각실',
        highlight: false,
      },
      {
        time: '14:30 - 14:50',
        title: '분야별 교실 배정, 이동',
        place: '이동',
        highlight: false,
      },
      {
        time: '15:00 - 18:00',
        title: '아이디어 도출 및 사업성 검토',
        place: '각 교실',
        highlight: false,
      },
    ],
  },
];

function Schedule() {
  return (
    <div className="main-bg">
      <nav className="main-nav">
        <div className="nav-left"></div>
        <ul className="nav-menu">
          <li><Link to="/">홈</Link></li>
          <li><Link to="/about">소개</Link></li>
          <li><Link to="/schedule">일정</Link></li>
          <li><Link to="/award">수상</Link></li>
        </ul>
        <button className="apply-btn">지원하기</button>
      </nav>
      <div className="timeline-container">
        {scheduleData.map((day, idx) => (
          <div key={day.date}>
            <div className="timeline-date-row">
              <span className="timeline-date">{day.date}</span>
              <span className="timeline-arrow">➔</span>
            </div>
            <div className="timeline-list">
              {day.items.map((item, i) => (
                <div className="timeline-item-row" key={i}>
                  <div className="timeline-dot-col">
                    <div className={`timeline-dot${item.highlight ? ' highlight' : ''}`}></div>
                    {i !== day.items.length - 1 && <div className="timeline-line"></div>}
                  </div>
                  <div className={`timeline-content${item.highlight ? ' highlight' : ''}`}>  
                    <div className="timeline-time">{item.time}</div>
                    <div className="timeline-desc">
                      <div className="timeline-title">{item.title}</div>
                      <div className="timeline-place">{item.place}</div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Schedule;
